const rawMission = $json.output.mission_score; 
const rawResponsibility = $json.output.responsibility_score;

const minRange = 0.10;
const maxRange = 0.90;

const adjust = (score) => {
    let adjustedScore = (score - minRange) / (maxRange - minRange);
    return Math.max(0, Math.min(1, adjustedScore));
};

const adjustedMission = adjust(rawMission);
const adjustedResponsibility = adjust(rawResponsibility);

return {
    mission_score: adjustedMission,
    responsibility_score: adjustedResponsibility,
};

const rawCosine = $json.ideal_score_cosine;

const min = 0.53;
const max = 0.67;

let adjustedCosine = (rawCosine - min) / (max - min);

adjustedCosine = Math.max(0, Math.min(1, adjustedCosine));

return  {
    ideal_score_cosine: adjustedCosine
  }
;

