const item = $json;

const missionScore = item.mission_score;
const responsibilityScore = item.responsibility_score;
const idealScoreCosine = item.ideal_score_cosine;

const weightMission = 0.4;
const weightResponsibility = 0.4;
const weightCosine = 0.2;

const finalWeightedScore = (missionScore * weightMission) + (responsibilityScore * weightResponsibility) + (idealScoreCosine * weightCosine);

return {
  final_weighted_score: finalWeightedScore,
  mission_weighted: missionScore * weightMission,
  responsibility_weighted: responsibilityScore * weightResponsibility,
  cosine_weighted: idealScoreCosine * weightCosine
};

const pillarScore = $input.item.json.final_weighted_score;
const bonusScore = $input.item.json.total_score;
const mission = $input.item.json.mission_weighted;
const responsibility = $input.item.json.responsibility_weighted;

const pillarMultiplier = 300;

const pillarScoreMultiplied = Math.round(pillarScore * pillarMultiplier);
let finalSumScore = pillarScoreMultiplied + bonusScore;

let signalTier = 'low';

if (mission >= 0.3 && responsibility >= 0.3) {
  signalTier = 'rare';
} else if (finalSumScore >= 400) {
  signalTier = 'high';
} else if (finalSumScore >= 280) {
  signalTier = 'mid';
}

return {
  signal_tier: signalTier,
  match_score: finalSumScore,
  job_url:$('Standardize input').item.json.job_url,
  metadata: {
    scores: {
      mission,
      responsibility,
      pillar: pillarScoreMultiplied,
      bonus: bonusScore,
    },
    flags: {
      remote_5_5: $json.bonus.remote_5_5,
      remote_4_1: $json.bonus.remote_4_1,
      remote_3_2: $json.bonus.remote_3_2,
      salary_120k_plus: $json.bonus.salary_120k_plus,
      salary_100k_120k: $json.bonus.salary_100k_120k,
      salary_90k_100k: $json.bonus.salary_90k_100k,
      team_under_5: $json.bonus.team_under_5,
      equity: $json.bonus.has_equity,
      car: $json.bonus.has_car,
      n8n: $json.bonus.uses_n8n,
      agents: $json.bonus.uses_agents,
      result_driven: $json.bonus.is_result_driven,
      flat_hierarchy: $json.bonus.is_flat_hierarchy
    }
  }
}
